def get_test_by_id(test_id):
    return TESTS.get(test_id)

def get_all_tests_info():
    return [{"id": test_id, "name": data["name"], "description": data.get("description", "")}
            for test_id, data in TESTS.items()]

def get_test_result(test_id, score, user_answers_indices=None):
    test_data = get_test_by_id(test_id)
    if not test_data:
        return None

    result_text = "–†–µ–∑—É–ª—å—Ç–∞—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω."
    consult_focus = "–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –¥–ª—è –∫–æ–Ω—Å—É–ª—å—Ç–∞—Ü–∏–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞."
    full_html = "<p>–ü–æ–ª–Ω—ã–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã.</p>"
    
    user_answers_str = ""
    user_answers_html_str = ""

    if user_answers_indices is not None:
        # –§–æ—Ä–º–∏—Ä—É–µ–º —Å—Ç—Ä–æ–∫—É —Å –æ—Ç–≤–µ—Ç–∞–º–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –¥–ª—è Telegram (Markdown)
        user_answers_str = "\n\nüìù *–í–∞—à–∏ –æ—Ç–≤–µ—Ç—ã:*\n"
        for i, ans_idx in enumerate(user_answers_indices):
            q_text = test_data["questions"][i]["text"].split('\n')[0] # –ö—Ä–∞—Ç–∫–∏–π –≤–æ–ø—Ä–æ—Å
            a_text = test_data["questions"][i]["options"][ans_idx]["text"]
            user_answers_str += f"_{i+1}\\. {q_text}_\n   –û—Ç–≤–µ—Ç: *{a_text}*\n" # –≠–∫—Ä–∞–Ω–∏—Ä—É–µ–º —Ç–æ—á–∫–∏ –¥–ª—è MarkdownV2

        # –§–æ—Ä–º–∏—Ä—É–µ–º HTML —Å—Ç—Ä–æ–∫—É —Å –æ—Ç–≤–µ—Ç–∞–º–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –¥–ª—è email
        user_answers_html_str = "<h3>–í–∞—à–∏ –æ—Ç–≤–µ—Ç—ã:</h3><ul>"
        for i, ans_idx in enumerate(user_answers_indices):
            q_text = test_data["questions"][i]["text"] # –ü–æ–ª–Ω—ã–π –≤–æ–ø—Ä–æ—Å –¥–ª—è email
            a_text = test_data["questions"][i]["options"][ans_idx]["text"]
            user_answers_html_str += f"<li><strong>{q_text}</strong><br>–û—Ç–≤–µ—Ç: <em>{a_text}</em></li>"
        user_answers_html_str += "</ul>"

    for res_info in test_data["results"]:
        low, high = res_info["range"]
        if low <= score <= high:
            summary_template = res_info.get("summary_template", "–†–µ–∑—É–ª—å—Ç–∞—Ç: {score} –±–∞–ª–ª–æ–≤.")
            result_text = summary_template.replace("{user_answers}", user_answers_str).replace("{score}", str(score))
            
            consult_focus = res_info.get("consultation_focus", "–û–±—Å—É–¥–∏—Ç–µ –≤–∞—à–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã —Å —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç–æ–º.")
            
            full_html_template = res_info.get("full_html_result_template", "<p>–†–µ–∑—É–ª—å—Ç–∞—Ç: {score} –±–∞–ª–ª–æ–≤.</p>{user_answers_html}")
            full_html = full_html_template.replace("{user_answers_html}", user_answers_html_str).replace("{score}", str(score))
            break
            
    return {"summary": result_text, "consultation_focus": consult_focus, "full_html_result": full_html}